INSERT INTO `npc_chat_bubbles` VALUES ('305', 'Да?', '193');
INSERT INTO `npc_chat_bubbles` VALUES ('306', 'Нам не будет счастья на этой земле.', '194');
INSERT INTO `npc_chat_bubbles` VALUES ('309', '...', '195');
INSERT INTO `npc_chat_bubbles` VALUES ('311', '...', '197');
INSERT INTO `npc_chat_bubbles` VALUES ('324', 'Э-эх! Беда с этими поссумами.', '206');
INSERT INTO `npc_chat_bubbles` VALUES ('334', 'Люблю.', '210');
INSERT INTO `npc_chat_bubbles` VALUES ('335', 'Когда же она придет...', '211');
INSERT INTO `npc_chat_bubbles` VALUES ('343', 'Там, сзади - очищенная жилая территория. Луг Лилиут и полуостров Солжрид совсем рядом, купишь участок?', '215');
INSERT INTO `npc_chat_bubbles` VALUES ('344', '[Лошадь недоверчиво косит на вас глазом и прядает ушами.]', '216');
INSERT INTO `npc_chat_bubbles` VALUES ('345', 'Капитан стражи Гден оставил меня ответственным, а всех остальных распустил! Такой ветер с моря приятный, как там луга у моря? А мне торчать тут...', '217');
INSERT INTO `npc_chat_bubbles` VALUES ('346', 'Часты песчаные бури, вести хозяйство сложнее обычного. Но ведь везде есть свои проблемы, не так ли?', '218');
INSERT INTO `npc_chat_bubbles` VALUES ('347', 'Чего-нибудь еще отведаете, наша вы прелесть? Вот. Что же за вкуснотища в кармашке Маргариты?', '219');
INSERT INTO `npc_chat_bubbles` VALUES ('348', 'В этом деле я мастер!', '220');
INSERT INTO `npc_chat_bubbles` VALUES ('349', 'Камня тут вдоволь, а деревьев нет. Так что мы нанялись работать на самых выгодных условиях.', '221');
INSERT INTO `npc_chat_bubbles` VALUES ('350', 'Говорят, у харнийцев камень добывают машины. Иногда я не понимаю, кто из нас выиграл войну, а кто проиграл.', '222');
INSERT INTO `npc_chat_bubbles` VALUES ('351', 'Камня тут, значит, много. Попробовали бы они добыть этот камень!', '223');
INSERT INTO `npc_chat_bubbles` VALUES ('352', 'Ох... Спина не разгибается.', '224');
INSERT INTO `npc_chat_bubbles` VALUES ('353', 'Хочешь купить амулет на счастье?', '225');
INSERT INTO `npc_chat_bubbles` VALUES ('354', 'Казалось бы, вот они, эти камни, подходи и бери. Но люди гибнут и гибнут, а дорога как была разрушенной, так и остается.', '226');
INSERT INTO `npc_chat_bubbles` VALUES ('355', 'Я слышу, как стонут во сне колоссы, когда их тела рвут на части кирками...', '227');
INSERT INTO `npc_chat_bubbles` VALUES ('356', 'По ночам в Мертвом ущелье кто-то стонет. Жуть!', '228');
INSERT INTO `npc_chat_bubbles` VALUES ('357', 'Может, и мне записаться в ополчение, а? Там большого ума не требуется.', '229');
INSERT INTO `npc_chat_bubbles` VALUES ('359', 'Добро пожаловать в нашу деревню.', '231');
INSERT INTO `npc_chat_bubbles` VALUES ('360', 'Сэндхевен - деревня рыбаков. Каменистую почву трудно возделывать, и полей у нас нет.', '232');
INSERT INTO `npc_chat_bubbles` VALUES ('361', 'Любопытно было бы взглянуть на лошадок из лагеря Ройстера. Ни разу не видел мэрианхольдскую породу.', '233');
INSERT INTO `npc_chat_bubbles` VALUES ('362', '...', '234');
INSERT INTO `npc_chat_bubbles` VALUES ('363', 'Может быть, стоит попробовать приручить бизонов?', '235');
INSERT INTO `npc_chat_bubbles` VALUES ('364', 'Если нужно починить снаряжение, обращайся.', '236');
INSERT INTO `npc_chat_bubbles` VALUES ('366', 'Говорят, в Мэрианхольде суматоха - одна из наследниц Великих Домов сбежала на поиски приключений! Представляешь?', '237');
INSERT INTO `npc_chat_bubbles` VALUES ('367', 'Погубят нас разбойники. Вот и ополчение ничего не смогло с ними сделать...', '238');
INSERT INTO `npc_chat_bubbles` VALUES ('370', 'Немного отдохну у костра - и пойду дальше.', '240');
INSERT INTO `npc_chat_bubbles` VALUES ('371', 'Я знаю столько способов обработки руды и камня, что здешним кузнецам не снилось.', '241');
INSERT INTO `npc_chat_bubbles` VALUES ('375', 'Лучше бы мы сражались с Черной Маской. Не понимаю, чего мы ждем?', '242');
INSERT INTO `npc_chat_bubbles` VALUES ('377', 'Чего желаешь?', '243');
INSERT INTO `npc_chat_bubbles` VALUES ('378', 'Самое главное в мире - это деньги.', '244');
INSERT INTO `npc_chat_bubbles` VALUES ('379', 'У меня твои ценные активы будут в безопасности.', '245');
INSERT INTO `npc_chat_bubbles` VALUES ('381', 'Будет время, взгляни на мой товар. Можно купить неплохую вещь за разумные деньги...', '246');
INSERT INTO `npc_chat_bubbles` VALUES ('382', 'Да хранит тебя богиня Нуи.', '247');
INSERT INTO `npc_chat_bubbles` VALUES ('383', 'Главное надо сохранить.', '248');
INSERT INTO `npc_chat_bubbles` VALUES ('384', 'Если дело касается гильдии, свяжись со мной.', '249');
INSERT INTO `npc_chat_bubbles` VALUES ('385', 'Дом Вейрдвинд уже много веков стоит на защите Мэрианхольда.', '250');
INSERT INTO `npc_chat_bubbles` VALUES ('388', 'Непременно прокатись на омнибусе. Многие приезжают издалека, чтобы увидеть это изобретение Академии механики.', '252');
INSERT INTO `npc_chat_bubbles` VALUES ('389', 'За этими вратами начинается Тропа Листьев - дорога, ведущая через землю Говорящих Камней и холмы Лилиот в Престол Полумесяца.', '253');
INSERT INTO `npc_chat_bubbles` VALUES ('390', 'За этими вратами начинается тракт, ведущий в Две Короны.', '254');
INSERT INTO `npc_chat_bubbles` VALUES ('391', 'За этими вратами находятся сады Торини - охотничьи угодья Великого Дома Норьетт.', '255');
INSERT INTO `npc_chat_bubbles` VALUES ('396', 'Сейчас ищу издателя.', '260');
INSERT INTO `npc_chat_bubbles` VALUES ('398', 'Та звезда моя, а та звезда - твоя.', '261');
INSERT INTO `npc_chat_bubbles` VALUES ('399', 'По какому делу?', '262');
INSERT INTO `npc_chat_bubbles` VALUES ('400', 'Великий Дом Тристе покровительствует науке и искусству.', '263');
INSERT INTO `npc_chat_bubbles` VALUES ('401', 'Лорд Симеон - достойный человек.', '264');
INSERT INTO `npc_chat_bubbles` VALUES ('402', 'Буду краток.', '265');
INSERT INTO `npc_chat_bubbles` VALUES ('404', 'Хочешь стать сильнее?', '267');
INSERT INTO `npc_chat_bubbles` VALUES ('405', 'С Каменистой равнины просят о помощи.', '268');
INSERT INTO `npc_chat_bubbles` VALUES ('406', 'Славный у тебя жеребец. Не желаешь ли его переодеть в достойные доспехи?', '269');
INSERT INTO `npc_chat_bubbles` VALUES ('407', 'М-е-е-е', '270');
INSERT INTO `npc_chat_bubbles` VALUES ('454', 'Здравствуй, мой друг! Остановись и послушай!', '271');
