INSERT INTO `npc_chat_bubbles` VALUES ('5770', 'Ой! Новая дичь!', '1890');
INSERT INTO `npc_chat_bubbles` VALUES ('5771', 'Привет.', '1891');
INSERT INTO `npc_chat_bubbles` VALUES ('5772', 'Товарищ, который не умер.', '1892');
INSERT INTO `npc_chat_bubbles` VALUES ('5773', 'Вы звали меня?', '1893');
