INSERT INTO `npc_chat_bubbles` VALUES ('6299', 'Где достойные роли? Где персонажи, достойные моего таланта? Взяться, что ли, самому за пьесу...', '159');
INSERT INTO `npc_chat_bubbles` VALUES ('6300', 'Надеюсь, Мэриан не исчезнет накануне состязаний. От нее чего угодно можно ожидать.', '161');
