INSERT INTO `npc_chat_bubbles` VALUES ('6414', 'Его Величество смеется который день не переставая, и народ начинает роптать. Над чем он смеется? Над нашими бедами?', '993');
INSERT INTO `npc_chat_bubbles` VALUES ('6415', 'Говорят, в Хмельных полях даже крокодилы, и те захмелевшие.', '993');
INSERT INTO `npc_chat_bubbles` VALUES ('6416', 'Придворные партии все время борются между собой, но людям от этого ни горячо, ни холодно.', '993');
