INSERT INTO `npc_chat_bubbles` VALUES ('6418', 'Говорят, будто великий Шао вновь достал столетнюю лютню. К добру ли это?', '993');
INSERT INTO `npc_chat_bubbles` VALUES ('6419', 'Свет в заливе Факелов давно погас. Из-под воды тянутся руки, холодные, скользкие руки...', '993');
